  @Override
  public void work() {
    setupEnvironment();
    myReporter.init();
    boolean doneSomething = false;
    //  for each project 
    Map<File, List<String>> mpsProjects = myWhatToDo.getMPSProjectFiles();
    for (File file : mpsProjects.keySet()) {
      if (!(file.getName().endsWith(MPSExtentions.DOT_MPS_PROJECT))) {
        continue;
      }

      FileMPSProject p = new FileMPSProject(file);
      p.init(new FileMPSProject.ProjectDescriptor(file));
      ModelAccess.instance().runReadAction(new Runnable() {
        public void run() {
          ClassLoaderManager.getInstance().updateClassPath();
          new ModuleMaker().make(IterableUtil.asCollection(MPSModuleRepository.getInstance().getModules()), new EmptyProgressMonitor());
        }
      });
      ModelAccess.instance().runWriteAction(new Runnable() {
        public void run() {
          ClassLoaderManager.getInstance().reloadAll(new EmptyProgressMonitor());
        }
      });
      p.projectOpened();

      info("Loaded project " + p);

      executeTask(p, new MpsWorker.ObjectsToProcess(Collections.singleton(p), new HashSet<SModule>(), new HashSet<SModel>()));

      p.projectClosed();
      disposeProject(p);
      dispose();
      doneSomething = true;
    }

    // the rest -- using dummy project 
    LinkedHashSet<SModule> modules = new LinkedHashSet<SModule>();
    LinkedHashSet<SModel> models = new LinkedHashSet<SModel>();
    collectFromModuleFiles(modules);
    collectFromModelFiles(models);
    MpsWorker.ObjectsToProcess go = new MpsWorker.ObjectsToProcess(Collections.EMPTY_SET, modules, models);
    if (go.hasAnythingToGenerate()) {
      Project project = createDummyProject();
      executeTask(project, go);
      doneSomething = true;
    }
    if (!(doneSomething)) {
      error("Could not find anything to generate.");
      myTestFailed = true;
    }

    myReporter.finishRun();
    cleanUp();
    dispose();
    showStatistic();
  }

  private void cleanUp() {
    for (Queue<File> dirs = QueueSequence.fromQueueAndArray(new LinkedList<File>(), new File(tmpPath)); QueueSequence.fromQueue(dirs).isNotEmpty();) {
      File dir = QueueSequence.fromQueue(dirs).removeFirstElement();
      dir.deleteOnExit();
      for (File f : dir.listFiles()) {
        if (f.isDirectory()) {
          QueueSequence.fromQueue(dirs).addLastElement(f);
        } else {
          f.deleteOnExit();
        }
      }
    }
    this.tmpPath = null;
    MapSequence.fromMap(path2tmp).clear();
  }

  private Iterable<IResource> collectResources(IOperationContext context, final Iterable<Project> projects, Iterable<SModule> modules, final Iterable<SModel> models) {
    final Wrappers._T<Iterable<SModule>> _modules = new Wrappers._T<Iterable<SModule>>(modules);
    final Wrappers._T<Iterable<SModel>> result = new Wrappers._T<Iterable<SModel>>(null);
    ModelAccess.instance().runReadAction(new Runnable() {
      public void run() {
        for (Project prj : projects) {
          if (isWholeProject(prj)) {
            _modules.value = Sequence.fromIterable(_modules.value).concat(Sequence.fromIterable(prj.getModules()));
          } else
          if (!(((FileMPSProject) prj).getDescriptor().getTestConfigurations().isEmpty())) {
            for (BaseTestConfiguration tconf : ((FileMPSProject) prj).getDescriptor().getTestConfigurations()) {
              try {
                result.value = Sequence.fromIterable(result.value).concat(Sequence.fromIterable((Iterable<SModelDescriptor>) tconf.getGenParams(prj, true).getModelDescriptors()));
              } catch (IllegalGeneratorConfigurationException e) {
                log("Error while reading configuration of project " + prj.getName(), e);
              }
            }
          } else {
            warning("No test configurations for project " + prj.getName());
          }
        }
        result.value = Sequence.fromIterable(result.value).concat(Sequence.fromIterable(_modules.value).translate(new ITranslator2<SModule, SModel>() {
          public Iterable<SModel> translate(SModule m) {
            return m.getModels();
          }
        }));
        result.value = Sequence.fromIterable(result.value).concat(Sequence.fromIterable(_modules.value).where(new IWhereFilter<SModule>() {
          public boolean accept(SModule it) {
            return it instanceof Language;
          }
        }).translate(new ITranslator2<SModule, Generator>() {
          public Iterable<Generator> translate(SModule it) {
            return ((Language) it).getGenerators();
          }
        }).translate(new ITranslator2<Generator, SModelDescriptor>() {
          public Iterable<SModelDescriptor> translate(Generator gen) {
            return gen.getOwnModelDescriptors();
          }
        }));
        result.value = Sequence.fromIterable(result.value).concat(Sequence.fromIterable(models));
      }
    });
    return new ModelsToResources(context, Sequence.fromIterable(result.value).where(new IWhereFilter<SModel>() {
      public boolean accept(SModel smd) {
        return GenerationFacade.canGenerate(smd);
      }
    })).resources(false);
  }

  private boolean isWholeProject(Project prj) {
    return Sequence.fromIterable(((Iterable<String>) myWhatToDo.getMPSProjectFiles().get(prj.getProjectFile()))).contains(ScriptProperties.WHOLE_PROJECT);
  }

  private IFile tmpFile(String path) {
    if (MapSequence.fromMap(path2tmp).containsKey(path)) {
      return FileSystem.getInstance().getFileByPath(MapSequence.fromMap(path2tmp).get(path));
    }
    int idx = path.indexOf("/");
    if (idx > 0) {
      throw new IllegalArgumentException("not an absolute path '" + path + "'");
    }
    idx = (idx < 0 ?
      path.indexOf(File.separator) :
      idx
    );
    if (idx > "C:\\".length() && path.indexOf(":") < 0) {
      throw new IllegalArgumentException("not an absolute path '" + path + "'");
    }
    String tmp = tmpPath + "/" + ((idx != 0 ?
      path.replace(":", "_w_") :
      path.substring(1)
    ));
    MapSequence.fromMap(path2tmp).put(path, tmp);
    return FileSystem.getInstance().getFileByPath(tmp);
  }

  private String pathOfTmpFile(IFile file) {
    String p = file.getPath();
    if (!(p.startsWith(tmpPath))) {
      throw new IllegalArgumentException("unknown tmp path '" + file.getParent() + "'");
    }
    p = p.substring(tmpPath.length() + 1);
    if (p.contains("_w_")) {
      return FileSystem.getInstance().getFileByPath(p.replace("_w_", ":")).getPath();
    }
    String prefix = (File.separatorChar == '/' ?
      "/" :
      "\\\\"
    );
    return FileSystem.getInstance().getFileByPath(prefix + p).getPath();
  }

