  private String[] getPerfomanceReportDestinations() {
    String reportType = myWhatToDo.getProperty(ScriptProperties.GENERATE_PERFORMANCE_REPORT);
    if (reportType == null || reportType.isEmpty()) {
      return new String[]{};
    }
    String[] reports = reportType.split(",+");
    return reports;
  }

